const OfferDetailBadge = ({icon, text}) => {
    return (
        <p className=" text-small-2 rounded-full px-4 py-2 bg-primary-20 flex gap-2 cursor-pointer border border-primary-20 hover:bg-primary-80 group">
            <i className={`${icon} text-primary-100 group-hover:text-black-100`}></i>
            <span>{text}</p>
        </p>
    )
}

export default  OfferDetailBadge;
