+    const user = personalInformation;
+    const navigate = useNavigate();
+
+    const {friends, handleFriends, countFollow, handleCountFollow, handleOffersForUser, allOffers} = useApp();
+
+    const [update, setUpdate] = useState(false)
+
+    const onClick = () => {
+        setUpdate(true)
